package com.mikankui.hanasite.kakeibo.test.db;import android.content.Context;import android.database.Cursor;import android.database.sqlite.SQLiteDatabase;import android.test.AndroidTestCase;import android.test.RenamingDelegatingContext;import com.mikankui.hanasite.kakeibo.db.DbOpenHelper;public class CK_DbOpenHelper extends AndroidTestCase {	Context context;	private static DbOpenHelper helper;	private static SQLiteDatabase db;	protected void setUpBeforeClass() throws Exception {	}	protected void tearDownAfterClass() throws Exception {		super.tearDown();		db.execSQL("DROP TABLE MEISAI;");	}	protected void setUp() throws Exception {		super.setUp();		context = new RenamingDelegatingContext(getContext(), "test_");		helper = new DbOpenHelper(context);		helper.setSQLPath("testdataOK0002");		db = helper.getWritableDatabase();	}	protected void tearDown() throws Exception {	}	/*	 * @see http://www.atmarkit.co.jp/ait/articles/1202/24/news144_2.html	 * 正常系	 * Case 0001 DBがNULLでないことを確認する	 * 	 * 異常系	 * 	 */	/**	 * Case 0001 DBがNULLでないことを確認する	 */	public void testDbOpenHelper_正常系_DBがNULLでないことを確認() {		assertNotNull(db);	}	/**	 * Case 0002 DBにMEISAIテーブルがあることを確認	 */	public void testDbOpenHelper_正常系_DBにMEISAIテーブルがあることを確認() {		String query = "SELECT COUNT(*) FROM sqlite_master WHERE type='table' AND name='MEISAI';";		Cursor c = db.rawQuery(query, null); 		c.moveToFirst();		int result = Integer.parseInt(c.getString(0));		assertEquals(result ,1);	}}